import React, { useContext, useRef, useState } from 'react'
import './Navbar.css'
import logo from '../Assets/logo2.png'
import cart_icon from '../Assets/cart_icon.png'
import { Link } from 'react-router-dom'
import { ShopContext } from '../../Context/ShopContext'
import nav_dropdown from '../Assets/dropdown_nav.png'

const Navbar = () => {

    const [menu,setMenu] = useState('Shop');
    const {getTotalCartItems} = useContext(ShopContext);
    const menuRef = useRef();

    const dropdown_toggle = (e) => {
      menuRef.current.classList.toggle('nav-menu-visible');
      e.target.classList.toggle('open');
    }

  return (
    <div className='navbar'>
      <div className="nav-logo">
        <img onClick={() => {window.location.replace('/')}} src={logo} alt="" />
        <p>CYBER TECH</p>
      </div>
      <img className='nav-dropdown' onClick={dropdown_toggle} src={nav_dropdown} alt="" />
      <ul ref={menuRef} className='nav-menu'>
        <li onClick={() => {setMenu("Shop")}}><Link to='/' style={{textDecoration: 'none'}}>Shop</Link>{menu==="Shop"?<hr/>:<></>}</li>
        <li onClick={() => {setMenu("Men")}}><Link to='/phones' style={{textDecoration: 'none'}}>Phones</Link>{menu==="Men"?<hr/>:<></>}</li>
        <li onClick={() => {setMenu("Women")}}><Link to='/tablets' style={{textDecoration: 'none'}}>Tablets</Link>{menu==="Women"?<hr/>:<></>}</li>
        <li onClick={() => {setMenu("Kids")}}><Link to='/kids' style={{textDecoration: 'none'}}>Kids</Link>{menu==="Kids"?<hr/>:<></>}</li>
      </ul>
      <div className="nav-login-cart">
        {localStorage.getItem('auth-token')
          ?<button onClick={() => {localStorage.removeItem('auth-token');window.location.replace('/')}}>Log Out</button>
          :<Link to='/login'><button>Login</button></Link>
        }
        <Link to='/cart'><img src={cart_icon} alt="" /></Link>
        <div className="nav-cart-count">{getTotalCartItems()}</div>
      </div>
    </div>
  )
}

export default Navbar
